package for_loop;

public class do_while {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a = 1;
do
{
	System.out.println(a);                //console 1 2 3 4 5 
	a++;
}
while (a<=5);

System.out.println();                        //for a space

int i = 50 ;
do {
	if (i!=45)
	{
		System.out.println(i);      //console 50 49 48 47 46 44 43 42 41 40
	}
	i--;
}
while (i>=40);
	}

}
