package for_loop;

public class while_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a = 20;
while (a>=10)
	{
	System.out.println(a);                   //console  20 19 ..... 10
	a--;
	}
System.out.println();                       //for a space 


int  b = 30 ;
while (b<=40)
{
	if((b!=32)&&(b!=35)&&(b!=37))
	{
		System.out.println(b);                  //console  30 31 33 34 36 38 39 40
	}
	b++;
}
	}

}
