package length_charAt;

public class length_charAt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String a = "selenium";
System.out.println(a.length());               //console 8
System.out.println(a.charAt(5));                 //console i


              //length()method is use to calculate length of string value.
                                          //length from 1 2 3 4 5 6................


               //charAt()method is used for find out character as per index position . 
                                       //index position means start from 0 1 2 3 4 5 6...............
	}

}
